mapper.update:
UPDATE notice_board
SET board_title = #{board_title},
	board_content = #{board_content},
	board_file = #{board_file}
WHERE board_num = #{board_num}

mapper.select:
SELECT *
FROM notice_board
WHERE board_title LIKE #{searchQuery}

SELECT *
FROM notice_board
WHERE DATE_FORMAT(board_date, '%Y-%m-%d') LIKE #{searchQuery};
