mapper.insert:
INSERT
INTO member
VALUES(
	null
	, #{member_id}
	, #{member_passwd}
	, #{member_name}
	, #{member_phone}
	, #{member_email}
	, 'N'
	, #{member_birth}
	, NOW()
	, null
	, 1
	, null
	, #{member_address1}
	, #{member_address2}
	, #{member_post_code}
)

mapper.select:
SELECT member_id
FROM member
WHERE member_name = #{member_name} AND member_phone = #{member_phone}

SELECT *
FROM member
WHERE member_id = #{member_id} AND member_name = #{member_name} AND member_phone = #{member_phone}

mapper.update:
UPDATE mailAuthInfo
SET auth_code = #{auth_code}
WHERE email = #{email}

mapper.select:
SELECT member_email
FROM member
WHERE member_email = #{email}

SELECT auth_code
FROM mailAuthInfo
WHERE email = #{email}

SELECT auth_code
FROM mailAuthInfo
WHERE email = #{member_email}

SELECT member_id
FROM member
WHERE member_email = #{member_email}

mapper.update:
UPDATE member
SET member_passwd = #{newPasswd}
WHERE member_email = #{member_email}

mapper.select:
SELECT member_id
FROM member
