mapper.select:
SELECT COUNT(*)
FROM question_board
WHERE member_id = #{id}

SELECT *
FROM question_board
WHERE member_id = #{id}
ORDER BY
	qna_num DESC
LIMIT
	#{startRow}
	,#{listLimit}

mapper.update:
UPDATE question_board
SET
	qna_type = #{qna_type},
	qna_title = #{qna_title},
	qna_content = #{qna_content}
WHERE qna_num = #{qna_num}

mapper.select:
 		SELECT *
 		FROM purchase_history
 		WHERE member_id = #{id}
 		ORDER BY
			pay_date DESC
		LIMIT
			#{startRow}
			, #{listLimit}

SELECT COUNT(*)
FROM review
WHERE member_id = #{id}

 		SELECT *
 		FROM review
 		WHERE member_id = #{id}
 		ORDER BY review_idx DESC
 		LIMIT
			#{startRow}
			, #{listLimit}

SELECT review_idx
FROM review_like
WHERE member_id = #{id}
